import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const App = (props) => {
  const [selected, setSelected] = useState(0)
  //console.log("anecdote: ", props.anecdotes[selected] )
  //console.log("selected: ", selected)

  return (
    <div>
      <Quote quote={props.anecdotes[selected]} />
      <Button handleClick={() => vote(selected) } text="vote" />
      <Button handleClick={() => {  setSelected(rando(selected)) }} text="Next" />
    </div>
  )
}


function rando(selected) {
  let numb = (Math.floor(Math.random() * 10) % anecdotes.length)
  //console.log("rando: ", numb)
  while (numb == selected) {
    numb = (Math.floor(Math.random() * 10) % anecdotes.length)
  }
  return (
    numb
  )
}

function vote(index) {
  console.log(votes, index)
  votes = [ ...votes ]
  votes[index] += 1
  console.log(votes[index])
  return votes
}

const anecdotes = [
  'If it hurts, do it more often',
  'Adding manpower to a late software project makes it later!',
  'The first 90 percent of the code accounts for the first 90 percent of the development time...The remaining 10 percent of the code accounts for the other 90 percent of the development time.',
  'Any fool can write code that a computer can understand. Good programmers write code that humans can understand.',
  'Premature optimization is the root of all evil.',
  'Debugging is twice as hard as writing the code in the first place. Therefore, if you write the code as cleverly as possible, you are, by definition, not smart enough to debug it.'
]

let votes = new Array((anecdotes.length)+1).join('0').split('').map(parseFloat)


const Button = (props) => {
  return (
    <>
      <button onClick={props.handleClick}>{props.text}</button>
    </>
  )
}

const Quote = (props) => {
  return (
    <>
      <h1>{props.quote} </h1>
      <p>{props.votes} </p>
    </>
  )
}

ReactDOM.render(
  <App anecdotes={anecdotes} />,
  document.getElementById('root')
)
