import React, { useState } from 'react'

const App = () => {
    const [ persons, setPersons ] = useState([
        { name: 'Arto Hellas', number: '040-123456' },
        { name: 'Ada Lovelace', number: '39-44-5323523' },
        { name: 'Dan Abramov', number: '12-43-234345' },
        { name: 'Mary Poppendieck', number: '39-23-6423122' }
    ]) 
    const [ newName, setNewName ] = useState('')
    const [ newNumber, setNewNumber ] = useState('')
    const [ newSearch, setNewSearch ] = useState('')



    const handleSearchChange = (event) => {
        setNewSearch(event.target.value)
    }

    const search = () => {
        return (
            persons.filter(person => 
                (person.name).toLowerCase().indexOf(newSearch.toLowerCase()) > -1
            )
        )
    }

    return (
      <div>
        <h2>Search</h2>
        <input value={newSearch} 
        onChange={handleSearchChange}
        />
        <PersonForm 
        persons={persons}
        setPersons={setPersons}
        newName={newName}
        setNewName={setNewName}
        newNumber={newNumber}
        setNewNumber={setNewNumber}
        />
        <h2>Numbers</h2>
        {search().map(person => 
            <Numbers key={person.number} name={person.name} number={person.number} />
            )}
      </div>
    )
}

const PersonForm = (props) => {
    const persons = props.persons
    const setPersons = props.persons
    const newName = props.newName
    const setNewName = props.setNewName
    const newNumber = props.NewNumber
    const setNewNumber = props.setNewNumber


    const nameExists = (input) => {
        const match = persons.map( person => {
            if (person.name === input)
                return true
        })
        if (match.includes(true))
            return true
    }

    const handleNewPerson = (event) => {
        event.preventDefault()
        if(nameExists(newName)) {
            alert(`${newName} already added`)
        } else {
        const personObject = {
            name: newName,
            number: newNumber
        }
        setPersons(persons.concat(personObject))
        }

        setNewName('')
        setNewNumber('')
    }

    const handleNameChange = (event) => {
        setNewName(event.target.value)
    }

    const handleNumberChange = (event) => {
        setNewNumber(event.target.value)
    }

    return (
        <>
        <h2>Add Person</h2>
        <form onSubmit={handleNewPerson} >
          <div>
            name: <input value={newName} 
            onChange={handleNameChange}
            />
          </div>
          <div>
            number: <input value={newNumber} 
            onChange={handleNumberChange}
            />
          </div>
          <div>
            <button type="submit">add</button>
          </div>
        </form>
        </>
    )
}

const Numbers = (props) => {

    return (
    <p>
        <b>{props.name}</b>: {props.number}
    </p>
    )
}

export default App
